package day01

import (
	"strings"
	"testing"
)

type testdata struct {
	input  string
	expect int
}

func TestSolve1(t *testing.T) {
	var tests = []testdata{
		{"(())", 0},
		{"()()", 0},
		{"(((", 3},
		{"(()(()(", 3},
		{"))(((((", 3},
		{"())", -1},
		{"))(", -1},
		{")))", -3},
		{")())())", -3},
	}

	for _, testCase := range tests {
		result, err := Solve1(strings.NewReader(testCase.input))
		if err != nil {
			t.Error(err)
		}
		if result != testCase.expect {
			t.Errorf("For \"%s\": expected %d, got %d",
				testCase.input,
				testCase.expect,
				result)
		}
	}
}

func BenchmarkSolve1Short(b *testing.B) {
	for n := 0; n < b.N; n++ {
		Solve1(strings.NewReader("((("))
	}
}

func BenchmarkSolve1Medium(b *testing.B) {
	for n := 0; n < b.N; n++ {
		Solve1(strings.NewReader("))((((("))
	}
}

