func TestSolve2(t *testing.T) {
	var tests = []testdata{
		{")", 1},
		{"()())", 5},
	}

	for _, testCase := range tests {
		result, err := Solve2(strings.NewReader(testCase.input))
		if err != nil {
			t.Error(err)
		}
		if result != testCase.expect {
			t.Errorf("For \"%s\": Got %d, expected %d\n", testCase.input, result, testCase.expect)
		}
	}
}
