package day01

import "testing"

type testSolve1data struct {
	input string
	floor int
}

func TestSolve1(t *testing.T) {
	var tests = []testSolve1data{
		{"(())", 0},
		{"()()", 0},
		{"(((", 3},
		{"(()(()(", 3},
		{"))(((((", 3},
		{"())", -1},
		{"))(", -1},
		{")))", -3},
		{")())())", -3},
	}

	for _, testCase := range tests {
		result := Solve1(testCase.input)
		if result != testCase.floor {
			t.Errorf("For %s: expected %d, got %d",
				testCase.input,
				testCase.floor,
				result)
		}
	}
}

func BenchmarkSolve1Short(b *testing.B) {
	for n := 0; n < b.N; n++ {
		Solve1("(((")
	}
}

func BenchmarkSolve1Medium(b *testing.B) {
	for n := 0; n < b.N; n++ {
		Solve1("))(((((")
	}
}

